@@ -33,4 +33,25 @@
 // Output: []
 // Explanation: There are no elements to be chunked so an empty array is returned.
 
 
+
+// SOLUTION 
+
+
+/**
+ * @param {Array} arr
+ * @param {number} size
+ * @return {Array}
+ */
+var chunk = function(arr, size) {
+  
+    const chunks = [];
+
+    for (let i = 0; i < arr.length; i += size) {
+        chunks.push(arr.slice(i, i + size)); // Create chunks of size 'size'
+    }
+
+     return chunks;
+
+
+};
